import React from 'react';

class componentDidUpdateMethod extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: 'this is previous state'
        }
    }
    componentDidMount() {
        setTimeout(() => {
            this.setState({
                name: 'this is current state'
            })
        }, 10000)
    }
    componentDidUpdate(prevState) {
        if(prevState.name !== this.state.name) {
            this.setState({
                name: 'Yes the state is changed'
            })
        }
    }
    render() {
        return (
            <div>
                <p>State was changed {this.state.name}</p>
            </div>
        )
    }
}

export default componentDidUpdateMethod;
